mapper.select:
SELECT COUNT(*) FROM BOARD
WHERE BOARD_CODE = #{boardCode}
AND BOARD_ST = 'N'

SELECT BOARD_NO, BOARD_CODE ,BOARD_TITLE, MEMBER_NICKNAME, PROFILE_IMG, TO_CHAR(CREATE_DT, 'YYYY-MM-DD') CREATE_DT, TO_CHAR(UPDATE_DT, 'YYYY-MM-DD') UPDATE_DT, READ_COUNT , BOARD_CONTENT,
(SELECT COUNT(*) FROM "LIKE_POST" L WHERE L.BOARD_NO = B.BOARD_NO) LIKE_COUNT
FROM "BOARD" B JOIN MEMBER USING(MEMBER_NO) WHERE BOARD_CODE = #{boardCode} AND BOARD_NO = #{boardNo}

SELECT BOARD_TITLE, MEMBER_NICKNAME, PROFILE_IMG, TO_CHAR(CREATE_DT, 'YYYY-MM-DD'), TO_CHAR(UPDATE_DT, 'YYYY-MM-DD'), READ_COUNT , BOARD_CONTENT,
(SELECT COUNT(*) FROM "LIKE_POST" L WHERE L.BOARD_NO = B.BOARD_NO) LIKE_COUNT
FROM "BOARD" B JOIN MEMBER USING(MEMBER_NO) WHERE BOARD_CODE = #{boardCode} AND BOARD_NO = #{boardNo}

mapper.update:
UPDATE BOARD SET
BOARD_TITLE = #{boardTitle},
BOARD_CONTENT = #{boardContent},
UPDATE_DT = SYSDATE
WHERE BOARD_CODE = #{boardCode}
AND BOARD_NO = #{boardNo}

UPDATE BOARD SET
BOARD_ST = 'Y'
WHERE BOARD_CODE = #{boardCode}
AND BOARD_NO = #{boardNo}
