mapper.select:
SELECT COUNT(*) FROM REVIEW
WHERE REVIEW_FLAG = 'N'
AND BOARD_CODE = #{boardCode}

mapper.update:
UPDATE REVIEW SET
    R_READ_COUNT = R_READ_COUNT + 1
WHERE REVIEW_NO = #{reviewNo}

UPDATE REVIEW
SET REVIEW_TITLE=#{reviewTitle},
    REVIEW_CONTENT=#{reviewContent}
WHERE REVIEW_NO=#{reviewNo}

UPDATE REVIEW
SET UPLOAD_IMAGE = #{uploadImage}
WHERE REVIEW_NO=#{reviewNo}

UPDATE REVIEW
SET REVIEW_FLAG = 'Y'
WHERE REVIEW_NO = #{reviewNo}
AND PRODUCT_NO = #{productNo}

UPDATE REVIEW
SET UPLOAD_IMAGE = DEFAULT
WHERE REVIEW_NO = #{reviewNo}

mapper.select:
SELECT COUNT(*) FROM PRODUCT_INQUIRY
WHERE INQUIRY_FLAG = 'N'
AND BOARD_CODE = #{boardCode}

mapper.update:
UPDATE PRODUCT_INQUIRY
SET INQUIRY_FLAG = 'Y'
WHERE INQUIRY_NO = #{inquiryNo}
AND PRODUCT_NO = #{productNo}

UPDATE PRODUCT_INQUIRY
SET INQUIRY_TITLE = #{inquiryTitle},
    INQUIRY_CONTENT = #{inquiryContent}
WHERE INQUIRY_NO = #{inquiryNo}

UPDATE PRODUCT_INQUIRY
SET UPLOAD_IMG = #{uploadImage}
WHERE INQUIRY_NO=#{inquiryNo}

UPDATE PRODUCT_INQUIRY
SET UPLOAD_IMG = DEFAULT
WHERE INQUIRY_NO=#{inquiryNo}

mapper.select:
SELECT PRODUCT_NO, MEMBER_NO, ITEM_COUNT, TOTAL_AMOUNT
FROM CART
WHERE ORDER_FL = 'N'
AND MEMBER_NO = #{memberNo}
AND PRODUCT_NO = #{productNo}

mapper.update:
UPDATE CART
SET ITEM_COUNT = #{quantity},
    TOTAL_AMOUNT = #{total}
WHERE PRODUCT_NO = #{productNo}
  AND MEMBER_NO = #{memberNo}

mapper.delete:
DELETE FROM CART
WHERE PRODUCT_NO = #{productNo}
AND MEMBER_NO = #{memberNo}

mapper.update:
UPDATE CART
SET ORDER_FL = 'Y',
    ORDER_NO = #{orderNo}
WHERE MEMBER_NO = #{memberNo}
  AND ORDER_FL = 'N'

mapper.select:
SELECT PRODUCT_NO, MEMBER_NO, ORDER_FL
FROM CART
WHERE MEMBER_NO = #{memberNo}
  AND ORDER_FL = 'Y'
  AND PRODUCT_NO = #{productNo}

mapper.update:
UPDATE REVIEW
SET R_READ_COUNT = R_READ_COUNT + 1
WHERE REVIEW_NO = #{reviewNo}
