mapper.select:
SELECT MEMBER_NO, MEMBER_NICKNAME, PROFILE_IMG
FROM MEMBER
WHERE MEMBER_NO = #{memberNo}

mapper.update:
UPDATE MEMBER
SET MEMBER_DEL_FL = 'Y'
WHERE MEMBER_NO = #{memberNo}

mapper.select:
SELECT ADMIN_ANSWER
FROM INQUIRY
WHERE BOARD_NO = #{boardNo}

mapper.update:
UPDATE MEMBER
SET PROFILE_IMG = #{profileImage}
WHERE MEMBER_NO = #{memberNo}
AND MEMBER_DEL_FL = 'N'
