mapper.select:
SELECT * FROM BOARD_IMG
WHERE BOARD_NO = #{boardNo}
ORDER BY IMG_LEVEL

SELECT * FROM ROUTINE
WHERE BOARD_NO = #{boardNo}
ORDER BY RT_LEVEL

SELECT COUNT(*) FROM
LIKE_POST
WHERE BOARD_NO = #{boardNo}
AND MEMBER_NO = #{memberNo}

mapper.delete:
DELETE FROM LIKE_POST
WHERE BOARD_NO = #{boardNo}
AND
MEMBER_NO = #{memberNo}

mapper.select:
SELECT COUNT(*) FROM
LIKE_POST
WHERE BOARD_NO = #{boardNo}

mapper.insert:
INSERT INTO BOARD_IMG SELECT SEQ_IMG_NO.NEXTVAL, A.* FROM(
SELECT #{img.imagePath} IMG_PATH, #{img.imageOriginal} IMG_ORIGINAL, #{img.imageReName} IMG_RENAME, #{img.boardNo} BOARD_NO, #{img.imageLevel} IMG_LEVEL FROM DUAL
)A

INSERT INTO ROUTINE SELECT SEQ_IMG_NO.NEXTVAL, A.* FROM(
SELECT #{rt.rtTitle} RT_TITLE, #{rt.rtContent} RT_CONTENT, #{rt.boardNo} BOARD_NO, #{rt.rtLevel} RT_LEVEL FROM DUAL
)A
